import React, { createContext, useContext, useState, useEffect } from 'react';

const UserContext = createContext();

export const useUserContext = () => useContext(UserContext);

export const UserProvider = ({ children }) => {
    const [user, setUser] = useState(() => {
        // Initially try to get user data from local storage
        const savedUser = localStorage.getItem('user');
        return savedUser ? JSON.parse(savedUser) : null;
    });

    useEffect(() => {
        // Whenever the user state changes, update local storage
        if (user) {
            localStorage.setItem('user', JSON.stringify(user));
        } else {
            localStorage.removeItem('user');
        }
    }, [user]);

    const login = async (email, password) => {
        try {
            const response = await fetch('/user/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });
            if (!response.ok) throw new Error('Login failed');
            const data = await response.json();
            setUser({ ...data, email }); // Store user data returned from server
        } catch (error) {
            console.error("Login error:", error);
            throw error; // Rethrow to let calling component handle it
        }
    };

    const logout = async () => {
        try {
            const response = await fetch('/user/logout');
            if (!response.ok) throw new Error('Logout failed');
            setUser(null); // Clear user from context
        } catch (error) {
            console.error("Logout error:", error);
            throw error; // Rethrow to let calling component handle it
        }
    };

    return (
        <UserContext.Provider value={{ user, login, logout }}>
            {children}
        </UserContext.Provider>
    );
};
