import React from 'react';
import { useNavigate } from 'react-router-dom';
import studentImage from './images/student.png';
import teacherImage from './images/teacher.png';

function RegisterPage() {
  const navigate = useNavigate();

  const handleStudentRegister = () => {
    navigate('/register/student'); 
  };

  const handleTeacherRegister = () => {
    navigate('/register/teacher');
  };

  const handleGoHome = () => {
    navigate('/');
  };

  const buttonStyle = {
    margin: '10px',
    padding: '20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    border: '1px solid #000',
    cursor: 'pointer'
  };

  return (
    <div style={{ backgroundColor: '#4CAF50', minHeight: '100vh' }}>
      <nav style={{ backgroundColor: '#4CAF50', padding: '10px 0' }}>
        <button onClick={handleGoHome} style={{ margin: '0 20px' }}>Home</button>
      </nav>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
        <div style={{ marginBottom: '40px' }}>
          <h1 style={{ color: 'white' }}>Register as:</h1>
        </div>
        <div style={{ display: 'flex' }}>
          <div onClick={handleStudentRegister} style={buttonStyle}>
            <img src={studentImage} alt="Student" style={{ width: '100px', marginBottom: '10px' }} />
            <span>Student</span>
          </div>
          <div onClick={handleTeacherRegister} style={buttonStyle}>
            <img src={teacherImage} alt="Teacher" style={{ width: '100px', marginBottom: '10px' }} />
            <span>Teacher</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default RegisterPage;
