import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navigation from '../components/Navigation'; // Assuming this path is correct
import InputField from '../components/InputField'; // Import the InputField component

function StudentRegisterPage() {
  const navigate = useNavigate();
  const currentYear = new Date().getFullYear();
  const [studentData, setStudentData] = useState({
    name: '',
    email: '',
    password: '',
    enrollmentYear: `${currentYear}`,
    studentId: '',
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudentData({ ...studentData, [name]: value });
    setError(''); // Clear error message on new input
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check for empty fields
    if (!studentData.name || !studentData.email || !studentData.password || !studentData.enrollmentYear || !studentData.studentId) {
      setError('All fields must be filled out');
      return;
    }

    console.log(studentData);
    navigate('/thank-you');
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '40px',
    marginTop: '20px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    borderRadius: '8px',
    backgroundColor: 'white',
    width: '100%',
    maxWidth: '400px',
  };

  const buttonStyle = {
    width: '100%',
    padding: '10px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
  };

  return (
    <div style={{ backgroundColor: '#F0F2F5', minHeight: '100vh' }}>
      <Navigation />
      <div style={{ padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <h1 style={{ color: '#333' }}>Student Registration</h1>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form onSubmit={handleSubmit} style={formStyle}>
          <InputField type="text" id="name" name="name" placeholder="Name" value={studentData.name} onChange={handleChange} />
          <InputField type="email" id="email" name="email" placeholder="Email" value={studentData.email} onChange={handleChange} />
          <InputField type="password" id="password" name="password" placeholder="Password" value={studentData.password} onChange={handleChange} />
          <InputField type="text" id="studentId" name="studentId" placeholder="Student ID" value={studentData.studentId} onChange={handleChange} />
          <div>
            <label htmlFor="enrollmentYear">Enrollment Year: </label>
            <select id="enrollmentYear" name="enrollmentYear" value={studentData.enrollmentYear} onChange={handleChange} style={{margin: '10px 0', padding: '10px'}}>
              {Array.from({ length: currentYear - 1999 }, (_, index) => 2000 + index).map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>
          <button type="submit" style={buttonStyle}>Register</button>
        </form>
      </div>
    </div>
  );
}

export default StudentRegisterPage;
