import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Navigation from "../components/Navigation";

function LoginPage() {
  const navigate = useNavigate();
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const [error, setError] = useState("");
  // 新增状态用于控制密码是否可见
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData({ ...loginData, [name]: value });
    setError(""); // Clear error message on new input
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!loginData.email || !loginData.password) {
      setError("All fields must be filled out");
      return;
    }

    if (loginData.email === "example@example.com" && loginData.password === "password") {
      navigate("/dashboard");
    } else {
      setError("Invalid email or password");
    }
  };

  // 切换密码可见性
  const togglePasswordVisibility = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  // 省略样式定义...

  return (
    <div style={{ backgroundColor: "#F0F2F5", minHeight: "100vh" }}>
      <Navigation />
      <div
        style={{
          padding: "20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h1 style={{ color: "#4CAF50" }}>Login</h1>
        {error && <div style={errorMessageStyle}>{error}</div>}
        <form
          onSubmit={handleSubmit}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            padding: "40px",
            marginTop: "20px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            borderRadius: "8px",
            backgroundColor: "white",
            width: "100%",
            maxWidth: "500px",
          }}
        >
          {/* 省略邮箱输入框... */}

          <div style={formFieldStyle}>
            <label htmlFor="password" style={labelStyle}>
              Password:
            </label>
            <input
              type={isPasswordVisible ? "text" : "password"} // 根据状态切换类型
              id="password"
              name="password"
              placeholder="Password"
              value={loginData.password}
              onChange={handleChange}
              style={inputStyle}
            />
            <button onClick={togglePasswordVisibility} style={{ marginLeft: "10px", padding: "0", background: "none", border: "none", cursor: "pointer" }}>
              {isPasswordVisible ? 'Hide' : 'Show'}
            </button>
          </div>

          {/* 省略登录按钮... */}
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
