import React, { useState, useEffect } from "react";
import { NavLink, Routes, Route, useRouteMatch } from "react-router-dom";
import Navigation from "../components/Navigation";
import Profile from "./Profile";
import OngoingQuizzes from "./OngoingQuizzes";
import CompletedQuizzes from "./CompletedQuizzes";
import "../styles/StudentDashboard.css";

function StudentDashboard() {
  let { path, url } = useRouteMatch();
  const [studentInfo, setStudentInfo] = useState({});
  const [ongoingQuizzes, setOngoingQuizzes] = useState([]);
  const [completedQuizzes, setCompletedQuizzes] = useState([]);

  // 获取学生信息
  useEffect(() => {
    const fetchStudentInfo = async () => {
      try {
        const response = await fetch('/student/info?studentId=1'); // 替换为您的API端点
        if (response.ok) {
          const data = await response.json();
          setStudentInfo(data);
        } else {
          // 处理错误
          console.error('Failed to fetch student info');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchStudentInfo();
  }, []);

  // 获取进行中的quizzes
  useEffect(() => {
    const fetchOngoingQuizzes = async () => {
      try {
        const response = await fetch('/quizzes/ongoing?studentId=1'); // 替换为您的API端点
        if (response.ok) {
          const data = await response.json();
          setOngoingQuizzes(data);
        } else {
          // 处理错误
          console.error('Failed to fetch ongoing quizzes');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchOngoingQuizzes();
  }, []);

  // 获取已完成的quizzes
  useEffect(() => {
    const fetchCompletedQuizzes = async () => {
      try {
        const response = await fetch('/quizzes/completed?studentId=1'); // 替换为您的API端点
        if (response.ok) {
          const data = await response.json();
          setCompletedQuizzes(data);
        } else {
          // 处理错误
          console.error('Failed to fetch completed quizzes');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchCompletedQuizzes();
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <Navigation />
      <div className="sidebar">
        <NavLink to={`${url}/profile`} activeClassName="active">Profile</NavLink>
        <NavLink to={`${url}/ongoing-quizzes`} activeClassName="active">Ongoing Quizzes</NavLink>
        <NavLink to={`${url}/completed-quizzes`} activeClassName="active">Completed Quizzes</NavLink>
      </div>
      <div className="content">
        <Switch>
          <Route path={`${path}/profile`}>
            <Profile studentInfo={studentInfo} />
          </Route>
          <Route path={`${path}/ongoing-quizzes`}>
            <OngoingQuizzes quizzes={ongoingQuizzes} />
          </Route>
          <Route path={`${path}/completed-quizzes`}>
            <CompletedQuizzes quizzes={completedQuizzes} />
          </Route>
        </Switch>
      </div>
    </div>
  );
}

export default StudentDashboard;
