import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Navigation from "../components/Navigation";

function StudentRegisterPage() {
  const navigate = useNavigate();
  const currentYear = new Date().getFullYear();
  const [studentData, setStudentData] = useState({
    name: "",
    email: "",
    password: "",
    enrollmentYear: `${currentYear}`,
    studentId: "",
  });

  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudentData({ ...studentData, [name]: value });
    setError(""); // Clear error message on new input
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !studentData.name ||
      !studentData.email ||
      !studentData.password ||
      !studentData.enrollmentYear ||
      !studentData.studentId
    ) {
      setError("All fields must be filled out");
      return;
    }
  
    fetch('http://localhost:5000/register/student', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(studentData),
    })
    .then(response => {
      if (!response.ok) {
        // If the server responds with a bad request, attempt to parse the error message
        // then reject the promise to be caught in the subsequent catch block
        return response.json().then(body => Promise.reject(body));
      }
      return response.json(); // Parse the successful JSON response
    })
    .then(data => {
      console.log(data);
      navigate("/thank-you");
    })
    .catch(error => {
      console.error('Error:', error);
      // Set the error message from the server if available, else a generic message
      setError(error.message || 'Failed to register');
    });
  }; // This closing brace was missing

  const formFieldStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    margin: "10px 0",
    width: "100%",
  };

  const labelStyle = {
    marginBottom: "8px",
    fontWeight: "bold",
    color: "#4CAF50",
  };

  const inputStyle = {
    padding: "12px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    marginBottom: "8px",
    width: "100%",
  };

  const hintStyle = {
    fontSize: "0.75rem",
    color: "#6c757d",
    width: "100%",
  };

  return (
    <div style={{ backgroundColor: "#F0F2F5", minHeight: "100vh" }}>
      <Navigation />
      <div
        style={{
          padding: "20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h1 style={{ color: "#4CAF50" }}>Student Registration</h1>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <form
          onSubmit={handleSubmit}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            padding: "40px",
            marginTop: "20px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            borderRadius: "8px",
            backgroundColor: "white",
            width: "100%",
            maxWidth: "500px", // Adjusted for better layout
          }}
        >
          {/* Form fields and submit button */}
        </form>
      </div>
    </div>
  );
}

export default StudentRegisterPage;
