import React from "react";
import Navigation from "../components/Navigation";
import { FaPlayCircle, FaRegCheckCircle } from 'react-icons/fa'; // 导入 FontAwesome 图标

function StudentDashboard() {
  const quizzes = {
    ongoing: [
      { id: 1, title: "Chapter 1: Introduction to Programming" },
      // 其他进行中的 quiz
    ],
    completed: [
      { id: 2, title: "Chapter 2: Data Structures" },
      // 其他已完成的 quiz
    ],
  };

  return (
    <div style={{ backgroundColor: "#F0F2F5", minHeight: "100vh" }}>
      <Navigation />
      <div
        style={{
          padding: "20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h1 style={{ color: "#4CAF50" }}>Student Dashboard</h1>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            padding: "20px",
            marginTop: "20px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
            borderRadius: "10px",
            backgroundColor: "white",
            width: "100%",
            maxWidth: "800px",
          }}
        >
          <div
            style={{
              width: "100%",
              marginBottom: "20px",
            }}
          >
            <h2 style={{ borderBottom: "2px solid #ccc", paddingBottom: "10px" }}>
              Ongoing Quizzes
            </h2>
            {quizzes.ongoing.map((quiz) => (
              <div 
                key={quiz.id} 
                style={{ 
                  padding: "10px", 
                  borderBottom: "1px solid #eee", 
                  display: "flex", 
                  alignItems: "center", 
                  justifyContent: "space-between",
                  cursor: "pointer",
                  transition: "background-color 0.3s",
                }}
                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#f9f9f9")}
                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
              >
                <span>{quiz.title}</span>
                <FaPlayCircle color="#4CAF50" title="Continue Quiz"/>
              </div>
            ))}
          </div>

          <div
            style={{
              width: "100%",
            }}
          >
            <h2 style={{ borderBottom: "2px solid #ccc", paddingBottom: "10px" }}>
              Completed Quizzes
            </h2>
            {quizzes.completed.map((quiz) => (
              <div key={quiz.id} style={{ padding: "10px", borderBottom: "1px solid #eee", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                <span>{quiz.title}</span>
                <FaRegCheckCircle color="#4CAF50" title="Quiz Completed"/>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default StudentDashboard;
