import React from 'react';
import { useNavigate } from 'react-router-dom';
import studentImage from './images/student.png';
import teacherImage from './images/teacher.png';

function RegisterPage() {
  const navigate = useNavigate();

  const handleStudentRegister = () => {
    navigate('/register/student');
  };

  const handleTeacherRegister = () => {
    navigate('/register/teacher');
  };

  const handleGoHome = () => {
    navigate('/');
  };

  const buttonStyle = {
    margin: '10px',
    padding: '20px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    border: '1px solid #ddd', // 更轻的边框颜色
    cursor: 'pointer',
    backgroundColor: '#FFF', // 白色背景
    borderRadius: '10px', // 圆角设计
    transition: 'all 0.3s ease-in-out', // 平滑过渡效果
  };

  const navStyle = {
    backgroundColor: '#4CAF50', // 更柔和的背景色
    padding: '20px 0',
    display: 'flex',
    justifyContent: 'center',
    borderBottom: '1px solid #ddd', // 底边框增加界定感
  };

  const navButtonStyle = {
    margin: '0 20px',
    background: 'none',
    border: '2px solid #4CAF50', // 绿色边框
    color: '#4CAF50', // 绿色文字
    padding: '10px 20px',
    borderRadius: '5px', // 圆角设计
    cursor: 'pointer',
    transition: 'all 0.2s', // 平滑过渡效果
  };

  return (
    <div style={{ backgroundColor: '#F0F2F5', minHeight: '100vh' }}>
      <nav style={navStyle}>
        <button onClick={handleGoHome} style={navButtonStyle}>Home</button>
      </nav>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '40px' }}>
        <div style={{ marginBottom: '40px' }}>
          <h1 style={{ color: '#333' }}>Register as:</h1> {/* 更深的文字颜色提高对比度 */}
        </div>
        <div style={{ display: 'flex' }}>
          <div onClick={handleStudentRegister} style={buttonStyle} onMouseOver={e => e.currentTarget.style.boxShadow = '0 8px 16px rgba(0,0,0,0.1)'} onMouseOut={e => e.currentTarget.style.boxShadow = 'none'}>
            <img src={studentImage} alt="Student" style={{ width: '100px', marginBottom: '10px' }} />
            <span>Student</span>
          </div>
          <div onClick={handleTeacherRegister} style={buttonStyle} onMouseOver={e => e.currentTarget.style.boxShadow = '0 8px 16px rgba(0,0,0,0.1)'} onMouseOut={e => e.currentTarget.style.boxShadow = 'none'}>
            <img src={teacherImage} alt="Teacher" style={{ width: '100px', marginBottom: '10px' }} />
            <span>Teacher</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default RegisterPage;
